import ExpenseItem from "./ExpenseItems";

function App() {
  const expenses = [
    {
      id: "e1",
      title: "Toilet Paper",
      amount: 94.12,
      date: new Date(2020, 7, 14),
    },
    { id: "e2", title: "New TV", amount: 799.49, date: new Date(2021, 2, 12) },
    {
      id: "e3",
      title: "Car Insurance",
      amount: 294.67,
      date: new Date(2021, 2, 28),
    },
    {
      id: "e4",
      title: "New Desk (Wooden)",
      amount: 450,
      date: new Date(2021, 5, 12),
    },
  ];

  return (
    <div>
      <h2>Let's get started!</h2>
      <ExpenseItem
        title={expenses[0].title}
        amount={expenses[0].amount}
        date={expenses[0].date}
      />
      <ExpenseItem
        title={expenses[1].title}
        amount={expenses[1].amount}
        date={expenses[1].date}
      />
      <ExpenseItem
        title={expenses[2].title}
        amount={expenses[2].amount}
        date={expenses[2].date}
      />
      <ExpenseItem
        title={expenses[3].title}
        amount={expenses[3].amount}
        date={expenses[3].date}
      />
    </div>
  );
}

export default App;

//컴포넌트는 마치 html 코드처럼 사용 할 수 있다.
//1. js파일을 생성한다.
//2. 자바스크립트 함수로 컴포넌트를 만들고 export
//3. 넣어주고 싶은 파일에 가서 import하고,
//4. html 코드처럼 넣어주기

//컴포넌트 reuse하는 방법
// 1. 컴포넌트 복붙하기 -> 하드코딩
// 2. 함수에 다른 매개변수를 넣어 다른 값을 얻는것처럼,
// 2-1. 대표 컴포넌트 안에 변수에 데이터를 넣고
// 2-2. 해당 컴포넌트 함수 안에 props 매개변수를 넣어주고
// 2-3. 그 데이터를 넣어줘야 하는 태그 안에 {props._변수이름_}을 넣어준다.

